INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01010001', '공공기관', 'Public Institution', '', '지역 주도의 균형발전을 통해 수도권 집중을 완화하고 지방이 자생력을 갖춘 시대를 지향하는 정책 기조. 중앙정부 주도에서 벗어나 지역이 스스로 성장동력을 창출하고 발전할 수 있는 체계를 구축하는 것을 목표로 함.', '4147179070a84d3887b97eb57085d850', 'C01010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01010002', '정부조직도', 'Government Organization Chart', '', '지방시대 정책을 총괄 조정하는 대통령 소속 위원회. 관계부처 협의, 정책 조정, 이행 점검 등의 역할을 수행하며 지방분권과 균형발전을 위한 컨트롤타워 역할.', '4147179070a84d3887b97eb57085d850', 'C01010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01010003', '공공기관 현황', 'Public Institution Status', '', '인구감소지역에 대한 국가의 특별한 지원을 규정한 법률. 인구감소지역 지정 기준, 국가와 지자체의 책무, 재정지원 근거 등을 명시.', '4147179070a84d3887b97eb57085d850', 'C01010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020001', '행정구역', 'Administrative District', '', '데이터·AI·클라우드 기반으로 공공서비스를 혁신하는 정부 운영 방식. 데이터 기반 의사결정, 서비스 통합·연계, 국민 맞춤형 서비스 제공을 지향.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020002', '지방자치단체', 'Local Government', '', '공공기관이 보유한 개인정보를 본인이 직접 관리·활용할 수 있도록 하는 제도. 개인정보 자기결정권 강화 및 데이터 기반 맞춤형 서비스 제공.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020003', '읍면동', 'Eup-Myeon-Dong', '', '보이스피싱 범죄를 탐지하고 예방하기 위한 AI 기반 음성 분석 기술. 통화 내용의 실시간 분석을 통해 의심 거래를 차단.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020004', '행정복지센터', 'Administrative Welfare Center', '', '읍·면·동에 설치된 주민 복지 서비스 통합 창구. 기존 주민센터의 명칭 변경.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01030001', '행정정보화', 'Administrative Informatization', '', '재난·안전 관리 체계를 효율적으로 개선하기 위한 국가 차원의 시스템. 컨트롤타워 일원화, 현장 중심 대응 체계, 과학기술 기반 예방 등을 포함.', '4147179070a84d3887b97eb57085d850', 'C01030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01030002', '정보자원', 'Information Resources', '', '국가 차원의 재난 대응 능력을 점검하고 훈련하는 통합 재난안전 훈련. 정부·지자체·민간이 함께 참여하는 범국가적 훈련.', '4147179070a84d3887b97eb57085d850', 'C01030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01030003', '공공데이터 개방', 'Open Public Data', '', '정보자원(시스템, 데이터, 네트워크 등)의 현황과 운영 상태. 행정기관 정보화 현황.', '4147179070a84d3887b97eb57085d850', 'C01030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01040001', '국가정책', 'National Policy', '', '개인이 고향이나 관심 있는 지방자치단체에 기부하면 세액공제와 답례품을 제공하는 제도. 지방재정 확충 및 지역경제 활성화 목적.', '4147179070a84d3887b97eb57085d850', 'C01040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01040002', '대민서비스', 'Public Service', '', '지방재정 조정을 위해 국세의 일부를 지방자치단체에 교부하는 재원. 보통교부세와 특별교부세로 구분되며 재정 격차 완화 목적.', '4147179070a84d3887b97eb57085d850', 'C01040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02010001', '교육통계연보', 'Education Statistics Yearbook', '', '매년 발간되는 교육 관련 주요 통계 자료집. 학교, 학생, 교원, 교육재정 등 전반적인 교육현황을 수록.', '4147179070a84d3887b97eb57085d850', 'C02010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02010002', '학교 통계', 'School Statistics', '', '학교 수, 학급 수, 학생 수 등 학교 기본 통계. 학교급별(유치원, 초중고, 대학) 현황 포함.', '4147179070a84d3887b97eb57085d850', 'C02010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02010003', '교원 통계', 'Teacher Statistics', '', '교원 수, 교원 1인당 학생 수, 교원 자격별 현황 등 교원 관련 통계.', '4147179070a84d3887b97eb57085d850', 'C02010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02010004', '학생 통계', 'Student Statistics', '', '학생 수, 재적 현황, 진학률, 졸업률 등 학생 관련 통계. 지역별·학교급별 구분.', '4147179070a84d3887b97eb57085d850', 'C02010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02010005', '교육재정', 'Education Finance', '', '교육재정 (Education Finance)', '4147179070a84d3887b97eb57085d850', 'C02010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02020001', '평생교육', 'Lifelong Education', '', '학교교육을 마친 후에도 계속되는 전 생애 학습 활동. 성인교육, 직업교육, 문화교육 등 포함.', '4147179070a84d3887b97eb57085d850', 'C02020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02020002', '평생교육기관', 'Lifelong Education Institution', '', '평생교육을 제공하는 기관. 평생학습관, 시민대학, 문화센터, 사회복지관 등.', '4147179070a84d3887b97eb57085d850', 'C02020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02020003', '평생학습강좌', 'Lifelong Learning Course', '', '평생교육기관에서 운영하는 강좌 프로그램. 인문교양, 문화예술, 시민참여, 직업능력 등.', '4147179070a84d3887b97eb57085d850', 'C02020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02020004', '성인문해교육', 'Adult Literacy Education', '', '성인의 기초 문해력 향상을 위한 교육. 한글 해득, 기초 수리력, 디지털 문해력 등.', '4147179070a84d3887b97eb57085d850', 'C02020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02020005', '직업능력개발', 'Vocational Training', '', '직업능력개발 (Vocational Training)', '4147179070a84d3887b97eb57085d850', 'C02020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02030001', '학교교육', 'School Education', '', '초·중·고등학교에서 이루어지는 정규 교육. 교육과정, 학업성취도, 생활지도 등 포함.', '4147179070a84d3887b97eb57085d850', 'C02030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02030002', '교육과정', 'Curriculum', '', '학교에서 가르치는 교과 내용과 방법의 체계. 국가수준 교육과정(2015개정, 2022개정 등).', '4147179070a84d3887b97eb57085d850', 'C02030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02030003', '학업성취도', 'Academic Achievement', '', '학생의 교육 목표 달성 정도를 측정하는 지표. 국가수준 학업성취도 평가(NAEA).', '4147179070a84d3887b97eb57085d850', 'C02030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02030004', '학교시설', 'School Facilities', '', '학교시설 (School Facilities)', '4147179070a84d3887b97eb57085d850', 'C02030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02030005', '교육복지', 'Education Welfare', '', '교육복지 (Education Welfare)', '4147179070a84d3887b97eb57085d850', 'C02030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02040001', '고등교육', 'Higher Education', '', '대학 및 대학원 수준의 고등교육. 학사·석사·박사 학위과정 포함.', '4147179070a84d3887b97eb57085d850', 'C02040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02040002', '대학정보공시', 'University Information Disclosure', '', '대학이 교육·연구·재정·학생 현황 등을 공개하는 제도. 대학 정보의 투명성 확보.', '4147179070a84d3887b97eb57085d850', 'C02040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02040003', '산학협력', 'Industry-Academia Cooperation', '', '산학협력 (Industry-Academia Cooperation)', '4147179070a84d3887b97eb57085d850', 'C02040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02050001', '유아교육', 'Early Childhood Education', '', '유아교육 (Early Childhood Education)', '4147179070a84d3887b97eb57085d850', 'C02050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02050002', '누리과정', 'Nuri Curriculum', '', '누리과정 (Nuri Curriculum)', '4147179070a84d3887b97eb57085d850', 'C02050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02060001', '특수교육', 'Special Education', '', '특수교육 (Special Education)', '4147179070a84d3887b97eb57085d850', 'C02060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T02060002', '영재교육', 'Gifted Education', '', '영재교육 (Gifted Education)', '4147179070a84d3887b97eb57085d850', 'C02060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010001', '의료기관', 'Medical Institution', '', '악성 종양으로, 비정상적인 세포의 무제한적 성장과 전이를 특징으로 하는 질환. 유전자 변이, 면역 이상 등 다양한 원인.', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010002', '병원', 'Hospital', '', '대장(결장 및 직장)에 발생하는 악성 종양. 선암이 대부분이며 식습관, 유전 등이 위험요인.', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010003', '의원', 'Clinic', '', '골수의 형질세포에서 발생하는 혈액암. M단백 생성, 골 병변, 빈혈 등의 증상.', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010004', '약국', 'Pharmacy', '', '암세포를 죽이거나 성장을 억제하는 약물 치료. 세포독성 항암제, 표적치료제, 면역항암제 등.', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010005', '의료시설', 'Medical Facility', '', '암을 조기에 발견하기 위한 검진. 대장내시경, 유방촬영, CT 등.', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010006', '의료장비', 'Medical Equipment', '', '암을 치료하기 위한 의학적 처치. 수술, 항암화학요법, 방사선치료, 면역치료 등.', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03020001', '의료이용 통계', 'Healthcare Utilization Statistics', '', '눈물이 부족하거나 과도하게 증발하여 안구 표면이 손상되는 질환. 눈물막 불안정성이 주요 원인.', '4147179070a84d3887b97eb57085d850', 'C03020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03020002', '진료통계', 'Medical Treatment Statistics', '', '수정체가 혼탁해져 시력이 저하되는 질환. 노화가 주요 원인이며 수술로 치료.', '4147179070a84d3887b97eb57085d850', 'C03020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03030001', '보건의료인력', 'Healthcare Personnel', '', '위산이 식도로 역류하여 불편감이나 합병증을 유발하는 질환. 속쓰림, 역류감 등의 증상.', '4147179070a84d3887b97eb57085d850', 'C03030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03030002', '의사', 'Physician', '', '내부 장기를 관찰하기 위해 내시경을 삽입하는 검사. 위내시경, 대장내시경 등.', '4147179070a84d3887b97eb57085d850', 'C03030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03030003', '간호사', 'Nurse', '', '위암을 조기에 발견하기 위한 검진. 위내시경, 조직검사 등.', '4147179070a84d3887b97eb57085d850', 'C03030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03040001', '건강보험', 'Health Insurance', '', '질병의 조기 발견과 예방을 위한 정기 검진. 국가건강검진, 암 검진 등.', '4147179070a84d3887b97eb57085d850', 'C03040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03040002', '의료급여', 'Medical Aid', '', '질병을 예방하고 건강을 증진하는 의학 분야. 예방접종, 건강증진, 환경보건 등.', '4147179070a84d3887b97eb57085d850', 'C03040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050001', '암', 'Cancer', '', '악성 종양으로, 비정상적인 세포의 무제한적 성장과 전이를 특징으로 하는 질환군. 대장암, 위암, 폐암, 유방암 등.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050002', '대장암', 'Colorectal Cancer', 'CRC', '대장(결장 및 직장)에 발생하는 악성 종양. 조기 발견 시 완치율이 높으며 대장내시경 검진이 중요.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050003', '안구건조증', 'Dry Eye Syndrome', 'DES', '눈물 분비 부족 또는 과도한 증발로 안구 표면이 손상되는 질환. 안구 불편감, 시력 저하 유발.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050004', '위식도역류질환', 'Gastroesophageal Reflux Disease', 'GERD', '위산이 식도로 역류하여 가슴쓰림과 불편감을 유발하는 만성 소화기 질환. 생활습관 개선과 약물치료로 관리.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03060001', '보건의료빅데이터', 'Healthcare Big Data', '', '보건의료 분야에서 수집·생성되는 대규모 데이터. 진료정보, 건강검진, 유전체정보, 의료영상 등.', '4147179070a84d3887b97eb57085d850', 'C03060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03060002', '의료영상데이터', 'Medical Imaging Data', '', 'X-ray, CT, MRI 등 의료영상 장비로 생성된 데이터. 진단·치료 계획 수립에 활용.', '4147179070a84d3887b97eb57085d850', 'C03060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04010001', '사회보장', 'Social Security', '', '고령 인구 비율에 따른 사회 단계 구분. 고령화사회(7% 이상), 고령사회(14% 이상), 초고령사회(20% 이상).', '4147179070a84d3887b97eb57085d850', 'C04010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04010002', '사회보험', 'Social Insurance', '', '65-74세 전기고령자(Young-old)와 75세 이상 후기고령자(Old-old)의 구분. 건강 상태와 사회활동 수준 차이.', '4147179070a84d3887b97eb57085d850', 'C04010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04010003', '공적부조', 'Public Assistance', '', '고령자가 고령자를 돌보는 돌봄 형태. 배우자 간 돌봄, 친구·이웃 간 돌봄 등.', '4147179070a84d3887b97eb57085d850', 'C04010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04020001', '복지시설', 'Welfare Facility', '', '국가가 운영하는 공적 연금 제도. 국민연금, 공무원연금, 군인연금 등.', '4147179070a84d3887b97eb57085d850', 'C04020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04020002', '노인복지시설', 'Elderly Welfare Facility', '', '연금 소득에 의존하여 생활하는 삶의 방식. 은퇴 후 소득원으로서의 연금 역할.', '4147179070a84d3887b97eb57085d850', 'C04020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04030001', '취약계층', 'Vulnerable Groups', '', '고령자 가구의 소득원(근로, 연금, 자산소득 등)과 지출 항목(생활비, 의료비 등) 구조 분석.', '4147179070a84d3887b97eb57085d850', 'C04030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04030002', '저소득층', 'Low-Income Groups', '', '빈곤층, 기초생활수급자 등 경제적으로 어려운 계층. 기초생활보장제도, 긴급복지지원 등의 대상.', '4147179070a84d3887b97eb57085d850', 'C04030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04040001', '고령사회', 'Aging Society', '', '전체 인구 중 65세 이상 인구가 14% 이상인 사회. 한국은 2018년에 고령사회 진입.', '4147179070a84d3887b97eb57085d850', 'C04040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04040002', '초고령사회', 'Super-Aged Society', '', '전체 인구 중 65세 이상 인구가 20% 이상인 사회. 한국은 2025년 초고령사회 진입 예상.', '4147179070a84d3887b97eb57085d850', 'C04040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T04040003', '노노케어', 'Senior-to-Senior Care', '', '고령자가 고령자를 돌보는 돌봄 형태. 배우자 간 돌봄, 친구·이웃 간 상호 돌봄 등.', '4147179070a84d3887b97eb57085d850', 'C04040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010001', '법령', 'Legislation', '', '법원의 재판 선례. 유사 사건의 재판 시 참고 기준.', '4147179070a84d3887b97eb57085d850', 'C05010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010002', '법률', 'Law', '', '대법원의 판결. 최종심 판결로 법적 구속력이 강함.', '4147179070a84d3887b97eb57085d850', 'C05010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010003', '시행령', 'Enforcement Decree', '', '헌법재판소의 결정. 위헌법률심판, 헌법소원 등.', '4147179070a84d3887b97eb57085d850', 'C05010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010004', '자치법규', 'Local Ordinance', '', '자치법규 (Local Ordinance)', '4147179070a84d3887b97eb57085d850', 'C05010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010005', '행정규칙', 'Administrative Rules', '', '행정규칙 (Administrative Rules)', '4147179070a84d3887b97eb57085d850', 'C05010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05020001', '판례', 'Case Law', '', '개인의 소득(근로소득, 사업소득, 기타소득 등)에 부과하는 세금. 누진세율 적용.', '4147179070a84d3887b97eb57085d850', 'C05020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05020002', '대법원 판례', 'Supreme Court Precedent', '', '법인의 소득(영업이익, 자산처분익 등)에 부과하는 세금. 법인세법에 근거.', '4147179070a84d3887b97eb57085d850', 'C05020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05020003', '헌법재판소 결정', 'Constitutional Court Decision', '', '재화와 용역의 공급(판매)에 부과하는 세금. 최종 소비자가 부담하는 간접세.', '4147179070a84d3887b97eb57085d850', 'C05020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05020004', '재판통계', 'Judicial Statistics', '', '상속 또는 증여받은 재산에 부과하는 세금. 부의 재분배 기능.', '4147179070a84d3887b97eb57085d850', 'C05020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05030001', '법률서비스', 'Legal Service', '', '국가공무원의 복무·권리·의무 등을 규정한 법률. 공직윤리, 징계, 신분보장 등.', '4147179070a84d3887b97eb57085d850', 'C05030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05030002', '법률상담', 'Legal Consultation', '', '행정절차의 공정성·투명성·신뢰성 확보를 위한 법률. 처분 절차, 의견 청취, 이유 제시 등.', '4147179070a84d3887b97eb57085d850', 'C05030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05030003', '법률구조', 'Legal Aid', '', '법률구조 (Legal Aid)', '4147179070a84d3887b97eb57085d850', 'C05030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05040001', '조세', 'Tax', '', '국가 또는 지방자치단체가 재정 수입을 위해 국민에게 부과하는 금전. 국세와 지방세로 구분.', '4147179070a84d3887b97eb57085d850', 'C05040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05040002', '종합부동산세', 'Comprehensive Real Estate Tax', '', '일정 금액 이상의 부동산을 보유한 자에게 부과하는 국세. 종부세로 약칭.', '4147179070a84d3887b97eb57085d850', 'C05040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05040003', '양도소득세', 'Capital Gains Tax', '', '부동산, 주식 등 자산의 양도로 발생한 소득에 부과하는 세금. 양도차익의 일정 비율 과세.', '4147179070a84d3887b97eb57085d850', 'C05040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05040004', '부가가치세', 'Value Added Tax', 'VAT', '재화와 용역의 공급(판매) 단계마다 부가되는 가치에 대해 부과하는 세금. 부가세, VAT로 약칭.', '4147179070a84d3887b97eb57085d850', 'C05040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05040005', '국세', 'National Tax', '', '국세 (National Tax)', '4147179070a84d3887b97eb57085d850', 'C05040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05040006', '지방세', 'Local Tax', '', '지방세 (Local Tax)', '4147179070a84d3887b97eb57085d850', 'C05040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05050001', '민법', 'Civil Law', '', '민법 (Civil Law)', '4147179070a84d3887b97eb57085d850', 'C05050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05050002', '상법', 'Commercial Law', '', '상법 (Commercial Law)', '4147179070a84d3887b97eb57085d850', 'C05050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05060001', '형법', 'Criminal Law', '', '형법 (Criminal Law)', '4147179070a84d3887b97eb57085d850', 'C05060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05060002', '행정심판', 'Administrative Appeal', '', '행정심판 (Administrative Appeal)', '4147179070a84d3887b97eb57085d850', 'C05060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06010001', '국가예산', 'National Budget', '', '중앙은행이 금융기관과 거래할 때 적용하는 정책금리. 한국은행 금융통화위원회에서 결정하며 시중금리의 기준이 됨.', '4147179070a84d3887b97eb57085d850', 'C06010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06010002', '분야별 예산', 'Budget by Sector', '', '중앙은행이 금융기관에 유동성을 지원하여 금융중개 기능을 활성화하는 대출제도. 대출 한도 및 금리는 한국은행이 결정.', '4147179070a84d3887b97eb57085d850', 'C06010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06010003', '부처별 예산', 'Budget by Ministry', '', '중앙은행이 유가증권(국채, 통안증권 등)을 매매하여 시중 통화량을 조절하는 정책 수단. 금융기관의 유동성을 직접 관리.', '4147179070a84d3887b97eb57085d850', 'C06010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06010004', '세출예산', 'Expenditure Budget', '', '높았던 물가 상승률이 목표 범위(한국은행 2%)로 돌아와 안정화되는 시기. 통화정책의 정상화를 의미.', '4147179070a84d3887b97eb57085d850', 'C06010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06010005', '세입예산', 'Revenue Budget', '', '식료품·에너지 등 변동성이 큰 품목을 제외한 물가지수. 기조적 물가 흐름 파악에 활용.', '4147179070a84d3887b97eb57085d850', 'C06010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06020001', '재정통계', 'Fiscal Statistics', '', '부동산 개발사업에 필요한 자금을 금융기관이 대출해주는 금융 상품. 사업성 평가 기반 대출로 담보 가치 변동 리스크 존재.', '4147179070a84d3887b97eb57085d850', 'C06020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06020002', '재정상태표', 'Statement of Financial Position', '', '금융시스템의 안정성 유지와 시스템 리스크 관리. 금융기관 건전성, 시장 안정성, 금융 인프라 안정성 포함.', '4147179070a84d3887b97eb57085d850', 'C06020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06020003', '국가결산', 'National Settlement', '', '회계연도가 종료된 후 세입·세출 결산을 확정하는 과정. 국가결산보고서 발간.', '4147179070a84d3887b97eb57085d850', 'C06020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06030001', '지방재정', 'Local Finance', '', '대기업과 중소기업, 금융기관과 서민이 함께 성장하는 금융 생태계. 동반성장, 상생협력의 금융 실천.', '4147179070a84d3887b97eb57085d850', 'C06030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06030002', '지방교부세', 'Local Allocation Tax', '', '금융 소외계층도 적절한 금융서비스를 이용할 수 있도록 하는 정책. 저신용자, 저소득층, 금융취약계층 지원.', '4147179070a84d3887b97eb57085d850', 'C06030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06030003', '재정자립도', 'Fiscal Independence Ratio', '', '소상공인의 금리 부담을 경감하기 위한 3가지 정책 패키지. 금리 인하, 상환 유예, 대환 지원 등.', '4147179070a84d3887b97eb57085d850', 'C06030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06040001', '국세', 'National Tax', '', '금융(Finance)과 기술(Technology)의 결합으로 혁신적인 금융서비스를 제공하는 산업. 모바일 결제, 인터넷전문은행, 로보어드바이저 등.', '4147179070a84d3887b97eb57085d850', 'C06040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06040002', '지방세', 'Local Tax', '', '핀테크 기업의 성장을 지원하기 위한 정부 및 민간의 투자 펀드. 초기 기업 투자, 기술 개발 지원.', '4147179070a84d3887b97eb57085d850', 'C06040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06040003', '금융소득', 'Financial Income', '', '핀테크 혁신펀드의 첫 번째 펀드.', '4147179070a84d3887b97eb57085d850', 'C06040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06050001', '기금', 'Fund', '', '근로자가 퇴직 후 생활안정을 위해 적립하는 연금. 확정급여형(DB)과 확정기여형(DC)으로 구분.', '4147179070a84d3887b97eb57085d850', 'C06050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06050002', '기금운용', 'Fund Management', '', '퇴직연금을 여러 사용자가 공동으로 적립·운용하는 기금 형태. 중소기업 근로자의 퇴직연금 가입률 제고.', '4147179070a84d3887b97eb57085d850', 'C06050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06060001', '통화정책', 'Monetary Policy', '', '한국과 호주 간 퇴직연금 제도를 논의하는 포럼. 양국 제도 비교, 정책 시사점 도출.', '4147179070a84d3887b97eb57085d850', 'C06060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06060002', '기준금리', 'Base Rate', '', '호주의 의무적 퇴직연금 제도. 사용자가 근로자 임금의 일정 비율을 연금 계좌에 적립.', '4147179070a84d3887b97eb57085d850', 'C06060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T06060003', '금융안정', 'Financial Stability', '', '호주 산업 슈퍼펀드(AIST)의 대표 거버넌스 모델. 사용자·근로자 동등 대표.', '4147179070a84d3887b97eb57085d850', 'C06060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07010001', '산업생산지수', 'Industrial Production Index', '', '디지털 기술을 기반으로 한 경제 활동. 플랫폼 경제, 공유경제, 데이터 경제 등.', '4147179070a84d3887b97eb57085d850', 'C07010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07010003', '기업통계', 'Business Statistics', '', '제조사가 유통 중간 단계를 거치지 않고 소비자에게 직접 판매하는 방식. 마진 개선, 고객 데이터 확보 가능.', '4147179070a84d3887b97eb57085d850', 'C07010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07010004', '산업구조', 'Industrial Structure', '', '정기적으로 상품이나 서비스를 제공받고 비용을 지불하는 경제 모델. 넷플릭스, 정기배송 등.', '4147179070a84d3887b97eb57085d850', 'C07010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07020001', '반도체산업', 'Semiconductor Industry', '', '스타트업이 성장할 수 있는 창업·투자·지원 생태계. 엑셀러레이터, VC, 정부 지원 등.', '4147179070a84d3887b97eb57085d850', 'C07020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07010002', '산업동향', 'Industrial Trends', '', '과학적 발견이나 공학적 혁신을 기반으로 한 첨단 기술. AI, 양자컴퓨팅, 바이오 등.', '4147179070a84d3887b97eb57085d850', 'C07010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07020002', '자동차산업', 'Automotive Industry', '', '벤처캐피탈(VC)에 투자하는 정부 출자 펀드. 민간 VC 육성 및 벤처 투자 활성화.', '4147179070a84d3887b97eb57085d850', 'C07020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07020003', '바이오산업', 'Bio Industry', '', '기업 간(B2B) 디지털 커머스 거래. 원자재, 부품, 서비스 등의 기업 간 온라인 거래.', '4147179070a84d3887b97eb57085d850', 'C07020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07030001', '서비스업', 'Service Industry', '', '새로운 형태의 럭셔리 소비 트렌드. 경험, 가치, 지속가능성 중시.', '4147179070a84d3887b97eb57085d850', 'C07030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07030002', '유통업', 'Distribution Industry', '', '상품을 최종 목적지(소비자)까지 배송하는 마지막 단계. 배송비 절감 및 배송 속도가 경쟁력 핵심.', '4147179070a84d3887b97eb57085d850', 'C07030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07040001', '고용통계', 'Employment Statistics', '', '커머스 운영을 위한 클라우드 기반 소프트웨어 서비스. Shopify, Cafe24 등.', '4147179070a84d3887b97eb57085d850', 'C07040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07040002', '일자리', 'Job', '', '마케팅 업무를 자동화하는 소프트웨어. 이메일 캠페인, 고객 세분화, 성과 분석 등.', '4147179070a84d3887b97eb57085d850', 'C07040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07040003', '실업률', 'Unemployment Rate', '', '고객 리뷰를 관리하는 솔루션. 리뷰 수집, 분석, 마케팅 활용.', '4147179070a84d3887b97eb57085d850', 'C07040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07040004', '노동시장', 'Labor Market', '', '가격과 혜택을 중시하며 여러 플랫폼을 비교하는 소비자. 할인, 쿠폰, 적립금 등에 민감.', '4147179070a84d3887b97eb57085d850', 'C07040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07040005', '최저임금', 'Minimum Wage', '', '소비자가 상품·서비스로부터 얻는 경제적·심리적 만족도. 가격 대비 효용.', '4147179070a84d3887b97eb57085d850', 'C07040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07050001', '창업', 'Startup', '', '디지털 기술을 기반으로 한 경제 활동 전반. 전자상거래, 플랫폼 비즈니스, 디지털 콘텐츠 산업 등.', '4147179070a84d3887b97eb57085d850', 'C07050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07050002', '벤처투자', 'Venture Investment', '', '플랫폼을 매개로 공급자와 수요자를 연결하는 경제 모델. 배달앱, 중개 플랫폼, 공유경제 등.', '4147179070a84d3887b97eb57085d850', 'C07050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07050003', '스타트업', 'Startup Company', '', '스타트업 (Startup Company)', '4147179070a84d3887b97eb57085d850', 'C07050001');
