INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07060001', '중소기업', 'SME', '', '이커머스 운영에 필요한 기술 솔루션. 결제, 배송, 재고관리, 고객관리 등.', '4147179070a84d3887b97eb57085d850', 'C07060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07060002', '소상공인', 'Small Business Owner', '', '주문부터 배송까지 물류 전 과정을 처리하는 서비스. 입고, 보관, 포장, 배송 대행.', '4147179070a84d3887b97eb57085d850', 'C07060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T07070001', '수출입', 'Import and Export', '', '수출입 (Import and Export)', '4147179070a84d3887b97eb57085d850', 'C07070001');
