INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08010001', '이커머스 플랫폼', 'E-commerce Platform', '', '재난의 예방·대비·대응·복구를 체계적으로 관리하는 활동. 국가재난관리체계, 재난관리책임기관 등.', '4147179070a84d3887b97eb57085d850', 'C08010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08010002', '온라인쇼핑몰', 'Online Shopping Mall', '', '재난 발생을 사전에 방지하기 위한 활동. 위험요인 제거, 시설물 정기점검, 안전교육 등.', '4147179070a84d3887b97eb57085d850', 'C08010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08010003', '오픈마켓', 'Open Market', '', '재난 발생 시 피해를 최소화하기 위한 긴급 대응 활동. 구조·구급, 긴급복구, 대피 등.', '4147179070a84d3887b97eb57085d850', 'C08010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08020001', 'D2C', 'Direct to Consumer', '', '생활·시설·교통 등 분야의 안전 위험을 관리하는 활동. 안전점검, 위험평가, 개선조치 등.', '4147179070a84d3887b97eb57085d850', 'C08020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08020002', '크로스보더', 'Cross-border Commerce', '', '건축물, 교량, 터널 등 시설물의 안전을 점검·관리하는 활동. 정기안전점검, 정밀안전진단.', '4147179070a84d3887b97eb57085d850', 'C08020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08030001', '간편결제', 'Simple Payment', '', '전시·사변 등 국가 비상사태에 대비하는 활동. 민방위 훈련, 비상대피, 물자 비축 등.', '4147179070a84d3887b97eb57085d850', 'C08030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08030002', 'BNPL', 'Buy Now Pay Later', '', 'BNPL (Buy Now Pay Later)', '4147179070a84d3887b97eb57085d850', 'C08030001');
