INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T08050001', '라이브커머스', 'Live Commerce', '', '라이브커머스 (Live Commerce)', '4147179070a84d3887b97eb57085d850', 'C08050001');
