INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T09010001', '문화재', 'Cultural Heritage', '', '보존할 가치가 있는 유·무형의 문화적 유산. 국가지정문화재, 시도지정문화재 등으로 구분.', '4147179070a84d3887b97eb57085d850', 'C09010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T09010002', '유형문화재', 'Tangible Cultural Heritage', '', '건조물, 전적, 회화, 조각 등 형태가 있는 문화재. 국보, 보물, 사적 등.', '4147179070a84d3887b97eb57085d850', 'C09010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T09010003', '무형문화재', 'Intangible Cultural Heritage', '', '연극, 음악, 무용, 공예기술 등 형태가 없는 문화재. 중요무형문화재, 인간문화재 등.', '4147179070a84d3887b97eb57085d850', 'C09010001');
