INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01010001', '공공기관', 'Public Institution', '', '지역 주도의 균형발전을 통해 수도권 집중을 완화하고 지방이 자생력을 갖춘 시대를 지향하는 정책 기조. 중앙정부 주도에서 벗어나 지역이 스스로 성장동력을 창출하고 발전할 수 있는 체계를 구축하는 것을 목표로 함.', '4147179070a84d3887b97eb57085d850', 'C01010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01010002', '정부조직도', 'Government Organization Chart', '', '지방시대 정책을 총괄 조정하는 대통령 소속 위원회. 관계부처 협의, 정책 조정, 이행 점검 등의 역할을 수행하며 지방분권과 균형발전을 위한 컨트롤타워 역할.', '4147179070a84d3887b97eb57085d850', 'C01010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01010003', '공공기관 현황', 'Public Institution Status', '', '인구감소지역에 대한 국가의 특별한 지원을 규정한 법률. 인구감소지역 지정 기준, 국가와 지자체의 책무, 재정지원 근거 등을 명시.', '4147179070a84d3887b97eb57085d850', 'C01010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020001', '행정구역', 'Administrative District', '', '데이터·AI·클라우드 기반으로 공공서비스를 혁신하는 정부 운영 방식. 데이터 기반 의사결정, 서비스 통합·연계, 국민 맞춤형 서비스 제공을 지향.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020002', '지방자치단체', 'Local Government', '', '공공기관이 보유한 개인정보를 본인이 직접 관리·활용할 수 있도록 하는 제도. 개인정보 자기결정권 강화 및 데이터 기반 맞춤형 서비스 제공.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020003', '읍면동', 'Eup-Myeon-Dong', '', '보이스피싱 범죄를 탐지하고 예방하기 위한 AI 기반 음성 분석 기술. 통화 내용의 실시간 분석을 통해 의심 거래를 차단.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01020004', '행정복지센터', 'Administrative Welfare Center', '', '읍·면·동에 설치된 주민 복지 서비스 통합 창구. 기존 주민센터의 명칭 변경.', '4147179070a84d3887b97eb57085d850', 'C01020001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01030001', '행정정보화', 'Administrative Informatization', '', '재난·안전 관리 체계를 효율적으로 개선하기 위한 국가 차원의 시스템. 컨트롤타워 일원화, 현장 중심 대응 체계, 과학기술 기반 예방 등을 포함.', '4147179070a84d3887b97eb57085d850', 'C01030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01030002', '정보자원', 'Information Resources', '', '국가 차원의 재난 대응 능력을 점검하고 훈련하는 통합 재난안전 훈련. 정부·지자체·민간이 함께 참여하는 범국가적 훈련.', '4147179070a84d3887b97eb57085d850', 'C01030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01030003', '공공데이터 개방', 'Open Public Data', '', '정보자원(시스템, 데이터, 네트워크 등)의 현황과 운영 상태. 행정기관 정보화 현황.', '4147179070a84d3887b97eb57085d850', 'C01030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01040001', '국가정책', 'National Policy', '', '개인이 고향이나 관심 있는 지방자치단체에 기부하면 세액공제와 답례품을 제공하는 제도. 지방재정 확충 및 지역경제 활성화 목적.', '4147179070a84d3887b97eb57085d850', 'C01040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T01040002', '대민서비스', 'Public Service', '', '지방재정 조정을 위해 국세의 일부를 지방자치단체에 교부하는 재원. 보통교부세와 특별교부세로 구분되며 재정 격차 완화 목적.', '4147179070a84d3887b97eb57085d850', 'C01040001');
