INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03030002', '의사', 'Physician', '', '내부 장기를 관찰하기 위해 내시경을 삽입하는 검사. 위내시경, 대장내시경 등.', '4147179070a84d3887b97eb57085d850', 'C03030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03030003', '간호사', 'Nurse', '', '위암을 조기에 발견하기 위한 검진. 위내시경, 조직검사 등.', '4147179070a84d3887b97eb57085d850', 'C03030001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03040001', '건강보험', 'Health Insurance', '', '질병의 조기 발견과 예방을 위한 정기 검진. 국가건강검진, 암 검진 등.', '4147179070a84d3887b97eb57085d850', 'C03040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03040002', '의료급여', 'Medical Aid', '', '질병을 예방하고 건강을 증진하는 의학 분야. 예방접종, 건강증진, 환경보건 등.', '4147179070a84d3887b97eb57085d850', 'C03040001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050001', '암', 'Cancer', '', '악성 종양으로, 비정상적인 세포의 무제한적 성장과 전이를 특징으로 하는 질환군. 대장암, 위암, 폐암, 유방암 등.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050002', '대장암', 'Colorectal Cancer', 'CRC', '대장(결장 및 직장)에 발생하는 악성 종양. 조기 발견 시 완치율이 높으며 대장내시경 검진이 중요.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050003', '안구건조증', 'Dry Eye Syndrome', 'DES', '눈물 분비 부족 또는 과도한 증발로 안구 표면이 손상되는 질환. 안구 불편감, 시력 저하 유발.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03050004', '위식도역류질환', 'Gastroesophageal Reflux Disease', 'GERD', '위산이 식도로 역류하여 가슴쓰림과 불편감을 유발하는 만성 소화기 질환. 생활습관 개선과 약물치료로 관리.', '4147179070a84d3887b97eb57085d850', 'C03050001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03060001', '보건의료빅데이터', 'Healthcare Big Data', '', '보건의료 분야에서 수집·생성되는 대규모 데이터. 진료정보, 건강검진, 유전체정보, 의료영상 등.', '4147179070a84d3887b97eb57085d850', 'C03060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03060002', '의료영상데이터', 'Medical Imaging Data', '', 'X-ray, CT, MRI 등 의료영상 장비로 생성된 데이터. 진단·치료 계획 수립에 활용.', '4147179070a84d3887b97eb57085d850', 'C03060001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010007', '병원명', 'HSPTL_NM', 'HSPTL_NM', '환자를 진찰, 치료하는 데에 필요한 설비를 갖추어 놓은 곳의 이름', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010008', '약품명', 'MDCN_NM', 'MDCN_NM', '병이나 상처 따위를 고치거나 예방하려고 먹거나 바르거나 주사하는 물질의 이름', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010009', '의사명', 'DR_NM', 'DR_NM', '일정한 자격을 가지고 병을 고치는 것을 직업으로 하는 사람의 이름', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010010', '담당의사명', 'TKCG_DR_NM', 'TKCG_DR_NM', '병원 등에서 환자의 진료를 맡은, 일정한 자격을 가지고 병을 고치는 것을 직업으로 하는 사람의 이름', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010011', '건강검진일자', 'HLTH_CKUP_YMD', 'HLTH_CKUP_YMD', '몸의 건강 상태를 검사하는 의학적 진찰을 하는 날짜', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010012', '의사면허번호', 'DR_LCNS_NO', 'DR_LCNS_NO', '의과 대학이나 의학 전문 대학원을 졸업하거나 졸업 예정인 사람이 의사 국가 고시에 합격하면 발급받는, 의사로서 의료 행위를 할 수 있게 자격을 허가하는 증서에 부여하는 식별 번호', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010013', '요양기관전화번호', 'RCPR_INST_TELNO', 'RCPR_INST_TELNO', '환자를 진료하거나 환자에게 약을 주는 기구나 조직의 전화번호', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010014', '의사수', 'DR_CNT', 'DR_CNT', '일정한 자격을 가지고 병을 고치는 것을 직업으로 하는 사람의 수', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010015', '간호사수', 'NRS_CNT', 'NRS_CNT', '의사의 진료를 돕고 환자를 돌보는 일을 하는, 법으로 자격이 부여된 사람의 수', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010016', '건의사항', 'SGST_MTTR', 'SGST_MTTR', '개인이나 단체가 의견이나 희망을 내놓은 일의 항목이나 내용', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010017', '문의사항', 'INQRY_MTTR', 'INQRY_MTTR', '물어서 의논하는 항목이나 내용', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010018', '유의사항', 'CTN_MTTR', 'CTN_MTTR', '마음에 새겨 두어 조심하며 관심을 가져야 하는 항목이나 내용', '4147179070a84d3887b97eb57085d850', 'C03010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T03010019', '주의사항', 'CUTN_MTTR', 'CUTN_MTTR', '마음에 새겨 두고 조심해야 하는 항목이나 내용', '4147179070a84d3887b97eb57085d850', 'C03010001');
