INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010020', '담당변호사명', 'TKCG_LWYR_NM', 'TKCG_LWYR_NM', '어떤 일을 맡은, 법률에 규정된 자격을 가지고 소송 당사자나 관계인의 의뢰 또는 법원의 명령에 따라 피고나 원고를 변론하며 그 밖의 법률에 관한 업무에 종사하는 사람의 이름', '4147179070a84d3887b97eb57085d850', 'C05010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010021', '등기우편번호', 'RG_ZIP', 'RG_ZIP', '우체국에서 우편물의 안전한 송달을 보증하려고 우편물의 인수ㆍ배달 과정을 기록한 우편번호', '4147179070a84d3887b97eb57085d850', 'C05010001');
