INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010023', '등기접수번호', 'RG_RCPT_NO', 'RG_RCPT_NO', '국가 기관이 법정 절차에 따라 등기부에 부동산이나 동산ㆍ채권 등의 담보 따위에 관한 일정한 권리관계를 적은 문서를 받는 일을 식별하기 위하여 부여한 번호', '4147179070a84d3887b97eb57085d850', 'C05010001');
INSERT INTO MC_TERM (TERM_ID, TERM_NAME, TERM_NAME_EN, ACRONYM, TERM_DESC, DIC_ID, README) VALUES ('T05010024', '등기접수일자', 'RG_RCPT_YMD', 'RG_RCPT_YMD', '국가 기관이 법정 절차에 따라 부동산이나 채권 등의 담보 따위에 관한 권리 신청이나 신고를 문서로 받은 날짜', '4147179070a84d3887b97eb57085d850', 'C05010001');
